import React, { useState } from 'react'
import { Link } from 'react-router-dom'

import PropTypes from 'prop-types'

import './drop-down.css'

const [isAdmin, setIsAdmin] = useState(false);
const [isLoggedIn, setIsLoggedIn] = useState(false);
var userDataObj;

const getAccountDetails = async(e) => {
  e.preventDefault();

  var url="http://localhost:8080/getAccountDetails";
  var request = new XMLHttpRequest(); // create a connection
  request.open('POST', url);
  request.send(); // send the http request
  request.onload = function() { // When the response comes invoke the following function
    let data = request.responseText; // store reponse in variable and convert to JSON object
    if (data.length == 0) {
      // set not logged in >> only show login tab
      setIsLoggedIn(false);
      document.getElementById("drop-down-logout").style.display = 'none';

    } else {
      userDataObj = JSON.parse(data);

      // set is logged in
      setIsLoggedIn(true);

      // if is admin >> sho admin tabs
      if (userDataObj.accountType == "ADMIN") {
        setIsAdmin(true);
      }
    }
  }
}

const DropDown = (props) => {

  return (
    <div className={`drop-down-sidebar ${props.rootClassName} `} onLoad={getAccountDetails}>
      <nav className="drop-down-nav">
        isLoggedIn? <Link to="/account" className="drop-down-text" id="drop-down-account">
          <span>Account</span>
        </Link> : null

        isAdmin? <Link to="/monthlysale" className="drop-down-text1" id="drop-down-sales">
          <span>Sale Reports</span>
        </Link> : null

        isAdmin? <Link to="/webusage" className="drop-down-text2" id="drop-down-webuse">
          <span>Web Usage Reports</span>
        </Link> : null

        isLoggedIn? null : <Link to="/loginPage" className="drop-down-text3" id="drop-down-login">
          <span>Login</span>
        </Link>
        <span className="drop-down-text4" id="drop-down-logout">Logout</span>
      </nav>
    </div>
  )
}

DropDown.defaultProps = {
  rootClassName: '',
}

DropDown.propTypes = {
  rootClassName: PropTypes.string,
}

export default DropDown
