import React, { useState } from 'react'
import { Link } from 'react-router-dom'

import PropTypes from 'prop-types'

import './drop-down.css'

const DropDown = (props) => {
  const [isAdmin, setIsAdmin] = useState(false)
  const [isLoggedIn, setIsLoggedIn] = useState(false)

  // useEffect(() => {
  //   fetch('/getAllAccounts')
  //    .then(response => response.json())
  //    .then(data => console.log(data));
  // })


  return (
    <div className={`drop-down-sidebar ${props.rootClassName} `}>
      <nav className="drop-down-nav">
        <Link to="/account" className="drop-down-text">
          <span>Account</span>
        </Link>
        <Link to="/monthlysale" className="drop-down-text1">
          <span>Sale Reports</span>
        </Link>
        <Link to="/webusage" className="drop-down-text2">
          <span>Web Usage Reports</span>
        </Link>
        <Link to="/loginPage" className="drop-down-text3">
          <span>Login</span>
        </Link>
        <span className="drop-down-text4">Logout</span>
      </nav>
    </div>
  )
}

DropDown.defaultProps = {
  rootClassName: '',
}

DropDown.propTypes = {
  rootClassName: PropTypes.string,
}

export default DropDown
