import React from 'react'
import { Link } from 'react-router-dom'

import { Helmet } from 'react-helmet'
import {useNavigate} from 'react-router-dom';
import Navbar from '../components/navbar'
import Footer from '../components/footer'
import './monthlysale.css'

const Monthlysale = (props) => {
	var userDataObj;
	const navigate = useNavigate();
	const checkbox1 = useRef();
    const checkbox2 = useRef();
    const checkbox3 = useRef();
    const checkbox4 = useRef();
    
	const monthlysale = async(e) => {	  
		e.preventDefault();
		const target = document.querySelector('#Ajaxresult');
	    let sortname;
	    if (checkbox1.current.checked) {
	        sortname = "Jan"
	        }
		else if (checkbox2.current.checked) {
	        sortname = "Feb"	       
	        }
	    else{
			//sortprice = None
			sortname = null
			}
	
	    var url="http://localhost:8080/catalog/sortByItemName?sort=" + sortname;
	    var request = new XMLHttpRequest(); // create a connection
	    request.open('POST', url);
	    request.send(); // send the http request
	    request.onload = function() { // When the response comes invoke the following function
	      let data = request.responseText; 
	      target.textContent = "Test output " + data;
	      if (data.length == 0) {
	        alert ("Item not found");
	      } else {
	        userDataObj = JSON.parse(data);
	        if (userDataObj[0] != null){
		        document.getElementById("item1-replace").innerHTML = userDataObj[0].itemName;
		        document.getElementById("price1-replace").innerHTML = "$" + userDataObj[0].price;
		        document.getElementById("color1-replace").innerHTML = userDataObj[0].color;
		        document.getElementById("category1-replace").innerHTML = userDataObj[0].category;
		        document.getElementById("brand1-replace").innerHTML = userDataObj[0].brand;
	        }else{
				document.getElementById("item1-replace").innerHTML = "ItemName";
		        document.getElementById("price1-replace").innerHTML = "price";
		        document.getElementById("color1-replace").innerHTML = "color";
		        document.getElementById("category1-replace").innerHTML = "category";
		        document.getElementById("brand1-replace").innerHTML = "brand";
			}
		    if (userDataObj[1] != null){
		        document.getElementById("item2-replace").innerHTML = userDataObj[1].itemName;
		        document.getElementById("price2-replace").innerHTML = "$" + userDataObj[1].price;
		        document.getElementById("color2-replace").innerHTML = userDataObj[1].color;
		        document.getElementById("category2-replace").innerHTML = userDataObj[1].category;
		        document.getElementById("brand2-replace").innerHTML = userDataObj[1].brand;
	        }else{
				document.getElementById("item2-replace").innerHTML = "ItemName";
		        document.getElementById("price2-replace").innerHTML = "price";
		        document.getElementById("color2-replace").innerHTML = "color";
		        document.getElementById("category2-replace").innerHTML = "category";
		        document.getElementById("brand2-replace").innerHTML = "brand";
			}
		    if (userDataObj[2] != null){
		        document.getElementById("item3-replace").innerHTML = userDataObj[2].itemName;
		        document.getElementById("price3-replace").innerHTML = "$" + userDataObj[2].price;
		        document.getElementById("color3-replace").innerHTML = userDataObj[2].color;
		        document.getElementById("category3-replace").innerHTML = userDataObj[2].category;
		        document.getElementById("brand3-replace").innerHTML = userDataObj[2].brand;
	        }else{
				document.getElementById("item3-replace").innerHTML = "ItemName";
		        document.getElementById("price3-replace").innerHTML = "price";
		        document.getElementById("color3-replace").innerHTML = "mycolor";
		        document.getElementById("category3-replace").innerHTML = "category";
		        document.getElementById("brand3-replace").innerHTML = "brand";
			}
	        navigate('/products');
	      }
	    }
  }
  return (
    <div className="monthlysale-container">
      <Helmet>
        <title>Monthlysale - 4413_LuxuryGlasses_EStore</title>
        <meta
          property="og:title"
          content="Monthlysale - 4413_LuxuryGlasses_EStore"
        />
      </Helmet>
      <Navbar></Navbar>
      <div className="monthlysale-main">
        <div className="monthlysale-container01">
          <h1 id="title-account-view" className="monthlysale-text">
            Monthly Sold items
          </h1>
          <div className="monthlysale-container02">
            <h3 id="edit-user-title" className="monthlysale-text01">
              Pick a month
            </h3>
            <form className="monthlysale-form">
              <div className="monthlysale-container03">
                <div className="monthlysale-container04">
                  <input
                    type="checkbox"
                    id="M-Jan"
                    name="month"
                    value="Jan"
                    className="monthlysale-checkbox"
                    ref={checkbox1}
                  />
                  <label className="monthlysale-text02">January</label>
                </div>
                <div className="monthlysale-container05">
                  <input
                    type="checkbox"
                    id="M-Feb"
                    name="month"
                    value="Feb"
                    className="monthlysale-checkbox01"
                    ref={checkbox2}
                  />
                  <label className="monthlysale-text03">February</label>
                </div>
                <div className="monthlysale-container06">
                  <input
                    type="checkbox"
                    id="M-Mar"
                    name="month"
                    value="Mar"
                    className="monthlysale-checkbox02"
                    ref={checkbox3}
                  />
                  <label className="monthlysale-text04">March</label>
                </div>
                <div className="monthlysale-container07">
                  <input
                    type="checkbox"
                    id="M-Apr"
                    name="month"
                    value="April"
                    className="monthlysale-checkbox03"
                    ref={checkbox4}
                  />
                  <label className="monthlysale-text05">April</label>
                </div>
                <div className="monthlysale-container08">
                  <input
                    type="checkbox"
                    id="M-May"
                    name="month"
                    value="May"
                    className="monthlysale-checkbox04"
                  />
                  <label className="monthlysale-text06">May</label>
                </div>
                <div className="monthlysale-container09">
                  <input
                    type="checkbox"
                    id="M-Jun"
                    name="month"
                    value="Jun"
                    className="monthlysale-checkbox05"
                  />
                  <label>June</label>
                </div>
              </div>
              <div className="monthlysale-container10">
                <div className="monthlysale-container11">
                  <input
                    type="checkbox"
                    id="M-Jul"
                    name="month"
                    value="Jul"
                    className="monthlysale-checkbox06"
                  />
                  <label className="monthlysale-text08">July</label>
                </div>
                <div className="monthlysale-container12">
                  <input
                    type="checkbox"
                    id="M-Aug"
                    name="month"
                    value="Aug"
                    className="monthlysale-checkbox07"
                  />
                  <label className="monthlysale-text09">August</label>
                </div>
                <div className="monthlysale-container13">
                  <input
                    type="checkbox"
                    id="M-Sep"
                    name="month"
                    value="Sep"
                    className="monthlysale-checkbox08"
                  />
                  <label className="monthlysale-text10">September</label>
                </div>
                <div className="monthlysale-container14">
                  <input
                    type="checkbox"
                    id="M-Oct"
                    name="month"
                    value="Oct"
                    className="monthlysale-checkbox09"
                  />
                  <label className="monthlysale-text11">October</label>
                </div>
                <div className="monthlysale-container15">
                  <input
                    type="checkbox"
                    id="M-Nov"
                    name="month"
                    value="Nov"                   
                    className="monthlysale-checkbox10"
                  />
                  <label className="monthlysale-text12">November</label>
                </div>
                <div className="monthlysale-container16">
                  <input
                    type="checkbox"
                    id="M-Dec"
                    name="month"
                    value="Dec"                  
                    className="monthlysale-checkbox11"
                  />
                  <label>December</label>
                </div>
              </div>
              <button type="submit" className="monthlysale-navlink button" onClick = {monthlysale}>
                Search
              </button>
            </form>
          </div>
          <div className="monthlysale-container17">
            <span className="monthlysale-text14">Output</span>
          </div>
        </div>
      </div>
      <Footer></Footer>
    </div>
  )
}

export default Monthlysale
